import React, { useState } from 'react';
import './Teacher.css';
import Sidebar from './SlideBar';

const TeacherDashboard = () => {
  const [activeTab, setActiveTab] = useState('students');
  
  // Sample data
  const classData = {
    attendance: 87,
    assignments: 92,
    averageGrade: 84,
    upcomingDeadlines: [
      { id: 1, title: 'Math Quiz', date: 'Apr 10, 2025', course: 'Mathematics' },
      { id: 2, title: 'Essay Submission', date: 'Apr 15, 2025', course: 'English' },
      { id: 3, title: 'Group Project', date: 'Apr 20, 2025', course: 'Science' }
    ],
    recentActivities: [
      { id: 1, activity: 'Posted new assignment', time: '2 hours ago', course: 'Mathematics' },
      { id: 2, activity: 'Graded essays', time: '5 hours ago', course: 'English' },
      { id: 3, activity: 'Created exam', time: '1 day ago', course: 'History' }
    ],
    students: [
      { id: 1, name: 'Emma Johnson', attendance: 95, grade: 92, status: 'Excellent' },
      { id: 2, name: 'Liam Smith', attendance: 85, grade: 78, status: 'Good' },
      { id: 3, name: 'Olivia Williams', attendance: 75, grade: 65, status: 'Needs Improvement' },
      { id: 4, name: 'Noah Brown', attendance: 90, grade: 88, status: 'Very Good' },
      { id: 5, name: 'Ava Jones', attendance: 98, grade: 95, status: 'Excellent' }
    ]
  };

  // Helper function to determine status class
  const getStatusClass = (status) => {
    switch(status) {
      case 'Excellent': return 'status-excellent';
      case 'Very Good': return 'status-very-good';
      case 'Good': return 'status-good';
      default: return 'status-improvement';
    }
  };

  // Helper function to determine progress bar class
  const getProgressClass = (value) => {
    if (value > 90) return 'progress-excellent';
    if (value > 80) return 'progress-good';
    if (value > 70) return 'progress-average';
    return 'progress-needs-improvement';
  };

  return (
    <div className="dashboard-container">
      {/* Header */}
      <Sidebar/>
      <header className="dashboard-header">
        <div className="header-content">
          <div className="logo-container">
            <div className="logo-circle">AE</div>
            <h1 className="logo-text">ATTEND EASE</h1>
          </div>
          <div className="header-actions">
            <div className="search-container">
              <span className="search-icon">
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </span>
              <input type="text" placeholder="Search..." className="search-input" />
            </div>
            <div className="user-actions">
              <span className="notification-icon">
                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 17h5l-1.405-1.405A2.032 2.032 0 0118 14.158V11a6.002 6.002 0 00-4-5.659V5a2 2 0 10-4 0v.341C7.67 6.165 6 8.388 6 11v3.159c0 .538-.214 1.055-.595 1.436L4 17h5m6 0v1a3 3 0 11-6 0v-1m6 0H9" />
                </svg>
              </span>
              <div className="user-avatar">
                <img src="/api/placeholder/40/40" alt="Teacher" />
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="main-content">
        {/* Navigation Tabs */}
        <div className="nav-tabs">
          {/* <button 
            className={`tab-button ${activeTab === 'overview' ? 'active' : ''}`}
            onClick={() => setActiveTab('overview')}
          >
            Overview
          </button> */}
          <button 
            className={`tab-button ${activeTab === 'students' ? 'active' : ''}`}
            onClick={() => setActiveTab('students')}
          >
            Students
          </button>
          {/* <button 
            className={`tab-button ${activeTab === 'assignments' ? 'active' : ''}`}
            onClick={() => setActiveTab('assignments')}
          >
            Assignments
          </button> */}
          <button 
            className={`tab-button ${activeTab === 'calendar' ? 'active' : ''}`}
            onClick={() => setActiveTab('calendar')}
          >
            Calendar
          </button>
          <button 
            className={`tab-button ${activeTab === 'reports' ? 'active' : ''}`}
            onClick={() => setActiveTab('reports')}
          >
            Reports
          </button>
        </div>

        {/* Dashboard Cards */}
       

        {/* Students Tab Content */}
        {activeTab === 'students' && (
          <div className="students-container">
            <div className="students-header">
              <h3 className="students-title">Student Performance</h3>
              <button className="add-student-btn">Add Student</button>
            </div>
            <div className="students-table-container">
              <table className="students-table">
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Attendance</th>
                    {/* <th>Grade</th> */}
                    {/* <th>Status</th> */}
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {classData.students.map(student => (
                    <tr key={student.id}>
                      <td>
                        <div className="student-info">
                          <div className="student-avatar"></div>
                          <div>
                            <p className="student-name">{student.name}</p>
                            <p className="student-id">ID: {student.id}000</p>
                          </div>
                        </div>
                      </td>
                      <td>
                        <div className="progress-info">
                          <span className="progress-value">{student.attendance}%</span>
                          <div className="mini-progress-container">
                            <div 
                              className={`mini-progress-bar ${getProgressClass(student.attendance)}`}
                              style={{ width: `${student.attendance}%` }}
                            ></div>
                          </div>
                        </div>
                      </td>
                      {/* <td>
                        <div className="progress-info">
                          <span className="progress-value">{student.grade}</span>
                          <div className="mini-progress-container">
                            <div 
                              className={`mini-progress-bar ${getProgressClass(student.grade)}`}
                              style={{ width: `${student.grade}%` }}
                            ></div>
                          </div>
                        </div>
                      </td> */}
                      {/* <td>
                        <span className={`status-badge ${getStatusClass(student.status)}`}>
                          {student.status}
                        </span>
                      </td> */}
                      <td>
                        <button className="view-details-btn">View Details</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}

        {/* Placeholder for other tabs */}
        {activeTab === 'assignments' && (
          <div className="placeholder-container">
            <h3 className="placeholder-title">Assignments Tab</h3>
            <p className="placeholder-text">Assignment management tools will appear here</p>
          </div>
        )}
        
        {activeTab === 'calendar' && (
          <div className="placeholder-container">
            <h3 className="placeholder-title">Calendar Tab</h3>
            <p className="placeholder-text">Calendar and scheduling tools will appear here</p>
          </div>
        )}
        
        {activeTab === 'reports' && (
          <div className="placeholder-container">
            <h3 className="placeholder-title">Reports Tab</h3>
            <p className="placeholder-text">Analytics and reporting tools will appear here</p>
          </div>
        )}
      </div>
    </div>
  );
};

