import React, { useState } from 'react';
import './WelcomePages.css';
import Sidebar from './SlideBar';

const StudentWelcome = ({ studentName = "Student" }) => {
  const [activeTab, setActiveTab] = useState('overview');
    
    // Sample data
    const classData = {
      attendance: 87,
      assignments: 92,
      averageGrade: 84,
      upcomingDeadlines: [
        { id: 1, title: 'Math Quiz', date: 'Apr 10, 2025', course: 'Mathematics' },
        { id: 2, title: 'Essay Submission', date: 'Apr 15, 2025', course: 'English' },
        { id: 3, title: 'Group Project', date: 'Apr 20, 2025', course: 'Science' }
      ],
      recentActivities: [
        { id: 1, activity: 'Posted new assignment', time: '2 hours ago', course: 'Mathematics' },
        { id: 2, activity: 'Graded essays', time: '5 hours ago', course: 'English' },
        { id: 3, activity: 'Created exam', time: '1 day ago', course: 'History' }
      ],
      students: [
        { id: 1, name: 'Emma Johnson', attendance: 95, grade: 92, status: 'Excellent' },
        { id: 2, name: 'Liam Smith', attendance: 85, grade: 78, status: 'Good' },
        { id: 3, name: 'Olivia Williams', attendance: 75, grade: 65, status: 'Needs Improvement' },
        { id: 4, name: 'Noah Brown', attendance: 90, grade: 88, status: 'Very Good' },
        { id: 5, name: 'Ava Jones', attendance: 98, grade: 95, status: 'Excellent' }
      ]
    };
  
    // Helper function to determine status class
    const getStatusClass = (status) => {
      switch(status) {
        case 'Excellent': return 'status-excellent';
        case 'Very Good': return 'status-very-good';
        case 'Good': return 'status-good';
        default: return 'status-improvement';
      }
    };
  
    // Helper function to determine progress bar class
    const getProgressClass = (value) => {
      if (value > 90) return 'progress-excellent';
      if (value > 80) return 'progress-good';
      if (value > 70) return 'progress-average';
      return 'progress-needs-improvement';
    };
  
  return (
    <div className="welcome-container student-welcome">
      <Sidebar/>
      {activeTab === 'overview' && (
          <div className="dashboard-overview">
            <div className="metrics-grid">
              {/* Attendance Card */}
              <div className="metric-card">
                <div className="metric-header">
                  <h3 className="metric-title">Class Attendance</h3>
                  <span className="metric-badge good">Good</span>
                </div>
                <div className="metric-value">
                  <p className="value">{classData.attendance}%</p>
                  {/* <p className="trend positive">â†‘ 2%</p> */}
                </div>
                <div className="progress-container">
                  <div className="progress-bar progress-blue" style={{ width: `${classData.attendance}%` }}></div>
                </div>
                <p className="metric-updated">Last updated: Today</p>
              </div>

              {/* Assignments Card */}
              {/* <div className="metric-card">
                <div className="metric-header">
                  <h3 className="metric-title">Assignment Completion</h3>
                  <span className="metric-badge excellent">Excellent</span>
                </div>
                <div className="metric-value">
                  <p className="value">{classData.assignments}%</p>
                  <p className="trend positive">â†‘ 5%</p>
                </div>
                <div className="progress-container">
                  <div className="progress-bar progress-purple" style={{ width: `${classData.assignments}%` }}></div>
                </div>
                <p className="metric-updated">Last updated: Today</p>
              </div> */}

              {/* Average Grade Card */}
              {/* <div className="metric-card">
                <div className="metric-header">
                  <h3 className="metric-title">Average Class Grade</h3>
                  <span className="metric-badge average">Average</span>
                </div>
                <div className="metric-value">
                  <p className="value">{classData.averageGrade}</p>
                  <p className="trend negative">â†“ 1%</p>
                </div>
                <div className="progress-container">
                  <div className="progress-bar progress-yellow" style={{ width: `${classData.averageGrade}%` }}></div>
                </div>
                <p className="metric-updated">Last updated: Today</p>
              </div> */}
            </div>

            <div className="info-grid">
              {/* <div className="info-card">
                <h3 className="info-title">Upcoming Deadlines</h3>
                <div className="deadline-list">
                  {classData.upcomingDeadlines.map(deadline => (
                    <div key={deadline.id} className="deadline-item">
                      <div className="deadline-info">
                        <h4 className="deadline-title">{deadline.title}</h4>
                        <p className="deadline-course">{deadline.course}</p>
                      </div>
                      <div className="deadline-date">
                        {deadline.date}
                      </div>
                    </div>
                  ))}
                </div>
                <button className="view-all">View All Deadlines</button>
              </div> */}

              {/* Recent Activities */}
              {/* <div className="info-card">
                <h3 className="info-title">Recent Activities</h3>
                <div className="activity-list">
                  {classData.recentActivities.map(activity => (
                    <div key={activity.id} className="activity-item">
                      <div className="activity-icon">
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
                        </svg>
                      </div>
                      <div className="activity-content">
                        <div className="activity-header">
                          <h4 className="activity-title">{activity.activity}</h4>
                          <span className="activity-time">{activity.time}</span>
                        </div>
                        <p className="activity-course">{activity.course}</p>
                      </div>
                    </div>
                  ))}
                </div>
                <button className="view-all">View All Activities</button>
              </div> */}
            </div>
          </div>
        )}
    </div>
  );
};

export default StudentWelcome;
